import { Component, OnInit } from '@angular/core';
import { CarrinhoService } from './services/carrinho.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  title = 'Gestão de Pedidos';
  abaSelecionada = 'catalogo';
  quantidadeCarrinho = 0;

  constructor(private carrinhoService: CarrinhoService) {}

  ngOnInit() {
    this.carrinhoService.itensCarrinho$.subscribe(itens => {
      this.quantidadeCarrinho = this.carrinhoService.obterQuantidadeTotal();
    });
  }

  selecionarAba(aba: string) {
    this.abaSelecionada = aba;
  }
}
<div class="app-container">
  <!-- Header -->
  <header class="app-header">
    <div class="header-content">
      <h1>{{ title }}</h1>
      <nav class="nav-menu">
        <button 
          class="nav-button" 
          [class.active]="abaSelecionada === 'catalogo'"
          (click)="selecionarAba('catalogo')">
          Catálogo
    </button>
        <button 
          class="nav-button" 
          [class.active]="abaSelecionada === 'carrinho'"
          (click)="selecionarAba('carrinho')">
          Carrinho ({{ quantidadeCarrinho }})
    </button>
        <button 
          class="nav-button" 
          [class.active]="abaSelecionada === 'pedidos'"
          (click)="selecionarAba('pedidos')">
          Meus Pedidos
    </button>
      </nav>
  </div>
  </header>

  <!-- Conteúdo -->
  <main class="app-content">
    <app-catalogo *ngIf="abaSelecionada === 'catalogo'"></app-catalogo>
    <app-carrinho *ngIf="abaSelecionada === 'carrinho'"></app-carrinho>
    <app-pedidos *ngIf="abaSelecionada === 'pedidos'"></app-pedidos>
  </main>
</div>

<router-outlet></router-outlet>
