import { Component, OnInit } from '@angular/core';
import { CarrinhoService } from '../../services/carrinho.service';
import { ApiService } from '../../services/api.service';
import { ItemCarrinho, CriarPedido } from '../../models/pedido.model';

@Component({
  selector: 'app-carrinho',
  templateUrl: './carrinho.component.html',
  styleUrls: ['./carrinho.component.css']
})
export class CarrinhoComponent implements OnInit {
  itens: ItemCarrinho[] = [];
  clienteId = 1; // Cliente padrão para simplificação
  criandoPedido = false;

  constructor(
    private carrinhoService: CarrinhoService,
    private apiService: ApiService
  ) { }

  ngOnInit() {
    this.carrinhoService.itensCarrinho$.subscribe(itens => {
      this.itens = itens;
    });
  }

  atualizarQuantidade(produtoId: number, event: any) {
    const quantidade = parseInt(event.target.value);
    if (quantidade > 0) {
      this.carrinhoService.atualizarQuantidade(produtoId, quantidade);
    }
  }

  removerItem(produtoId: number) {
    this.carrinhoService.removerItem(produtoId);
  }

  obterTotal(): number {
    return this.carrinhoService.obterTotal();
  }

  formatarPreco(preco: number): string {
    return preco.toLocaleString('pt-BR', {
      style: 'currency',
      currency: 'BRL'
    });
  }

  finalizarPedido() {
    if (this.itens.length === 0) {
      alert('Carrinho vazio!');
      return;
    }

    this.criandoPedido = true;

    const pedido: CriarPedido = {
      clienteId: this.clienteId,
      itens: this.itens.map(item => ({
        produtoId: item.produto.id,
        quantidade: item.quantidade
      }))
    };

    this.apiService.criarPedido(pedido).subscribe({
      next: (resultado) => {
        if (resultado.sucesso) {
          alert(`Pedido criado com sucesso! ID: ${resultado.dados}`);
          this.carrinhoService.limparCarrinho();
        } else {
          alert('Erro ao criar pedido: ' + resultado.mensagem);
        }
        this.criandoPedido = false;
      },
      error: (error) => {
        alert('Erro ao criar pedido: ' + error.message);
        this.criandoPedido = false;
      }
    });
  }

  limparCarrinho() {
    if (confirm('Deseja limpar o carrinho?')) {
      this.carrinhoService.limparCarrinho();
    }
  }
}
<div class="carrinho-container">
  <h2>Carrinho de Compras</h2>
  
  <div *ngIf="itens.length === 0" class="carrinho-vazio">
    <p>Seu carrinho está vazio.</p>
    <p>Adicione produtos do catálogo para continuar.</p>
  </div>
  
  <div *ngIf="itens.length > 0">
    <div class="itens-carrinho">
      <div class="item-carrinho" *ngFor="let item of itens">
        <div class="item-info">
          <h4>{{ item.produto.nome }}</h4>
          <p class="item-sku">SKU: {{ item.produto.sku }}</p>
          <p class="item-preco">{{ formatarPreco(item.produto.precoBase) }}</p>
        </div>
        
        <div class="item-quantidade">
          <label>Quantidade:</label>
          <input 
            type="number" 
            min="1" 
            [max]="item.produto.estoqueAtual"
            [value]="item.quantidade"
            (change)="atualizarQuantidade(item.produto.id, $event)">
        </div>
        
        <div class="item-total">
          <p class="subtotal">{{ formatarPreco(item.produto.precoBase * item.quantidade) }}</p>
        </div>
        
        <div class="item-actions">
          <button class="btn-remover" (click)="removerItem(item.produto.id)">
            Remover
          </button>
        </div>
      </div>
    </div>
    
    <div class="carrinho-resumo">
      <div class="total-geral">
        <h3>Total: {{ formatarPreco(obterTotal()) }}</h3>
      </div>
      
      <div class="carrinho-actions">
        <button class="btn-limpar" (click)="limparCarrinho()">
          Limpar Carrinho
        </button>
        
        <button 
          class="btn-finalizar" 
          (click)="finalizarPedido()"
          [disabled]="criandoPedido">
          {{ criandoPedido ? 'Criando Pedido...' : 'Finalizar Pedido' }}
        </button>
      </div>
    </div>
  </div>
</div>
