import { Component, OnInit } from '@angular/core';
import { ApiService } from '../../services/api.service';
import { Pedido, StatusPedido } from '../../models/pedido.model';

@Component({
  selector: 'app-pedidos',
  templateUrl: './pedidos.component.html',
  styleUrls: ['./pedidos.component.css']
})
export class PedidosComponent implements OnInit {
  pedidos: Pedido[] = [];
  carregando = false;
  erro = '';
  pedidoDetalhes: Pedido | null = null;
  mostrarDetalhes = false;

  constructor(private apiService: ApiService) { }

  ngOnInit() {
    this.carregarPedidos();
  }

  carregarPedidos() {
    this.carregando = true;
    this.erro = '';

    this.apiService.obterPedidos().subscribe({
      next: (resultado) => {
        if (resultado.sucesso) {
          this.pedidos = resultado.dados;
        } else {
          this.erro = resultado.mensagem;
        }
        this.carregando = false;
      },
      error: (error) => {
        this.erro = 'Erro ao carregar pedidos: ' + error.message;
        this.carregando = false;
      }
    });
  }

  verDetalhes(pedido: Pedido) {
    this.pedidoDetalhes = pedido;
    this.mostrarDetalhes = true;
  }

  fecharDetalhes() {
    this.mostrarDetalhes = false;
    this.pedidoDetalhes = null;
  }

  formatarPreco(preco: number): string {
    return preco.toLocaleString('pt-BR', {
      style: 'currency',
      currency: 'BRL'
    });
  }

  formatarData(data: string): string {
    return new Date(data).toLocaleString('pt-BR');
  }

  obterStatusTexto(status: StatusPedido): string {
    switch (status) {
      case StatusPedido.Rascunho: return 'Rascunho';
      case StatusPedido.Confirmado: return 'Confirmado';
      case StatusPedido.Cancelado: return 'Cancelado';
      case StatusPedido.Faturado: return 'Faturado';
      default: return 'Desconhecido';
    }
  }

  obterClasseStatus(status: StatusPedido): string {
    switch (status) {
      case StatusPedido.Rascunho: return 'status-rascunho';
      case StatusPedido.Confirmado: return 'status-confirmado';
      case StatusPedido.Cancelado: return 'status-cancelado';
      case StatusPedido.Faturado: return 'status-faturado';
      default: return '';
    }
  }
}
<div class="pedidos-container">
  <h2>Meus Pedidos</h2>
  
  <div *ngIf="carregando" class="loading">
    Carregando pedidos...
  </div>
  
  <div *ngIf="erro" class="error">
    {{ erro }}
    <button (click)="carregarPedidos()">Tentar Novamente</button>
  </div>
  
  <div class="pedidos-lista" *ngIf="!carregando && !erro">
    <div class="pedido-card" *ngFor="let pedido of pedidos">
      <div class="pedido-header">
        <div class="pedido-numero">
          <h3>Pedido {{ pedido.numero }}</h3>
          <span class="status" [ngClass]="obterClasseStatus(pedido.status)">
            {{ obterStatusTexto(pedido.status) }}
          </span>
        </div>
        <div class="pedido-data">
          {{ formatarData(pedido.criadoEm) }}
        </div>
      </div>
      
      <div class="pedido-itens">
        <h4>Itens do Pedido:</h4>
        <div class="item" *ngFor="let item of pedido.itens">
          <div class="item-info">
            <span class="item-nome">{{ item.produtoNome }}</span>
            <span class="item-sku">SKU: {{ item.produtoSku }}</span>
          </div>
          <div class="item-quantidade">
            Qtd: {{ item.quantidade }}
          </div>
          <div class="item-preco">
            {{ formatarPreco(item.precoUnitario) }}
          </div>
          <div class="item-total">
            {{ formatarPreco(item.totalItem) }}
          </div>
        </div>
      </div>
      
      <div class="pedido-totais">
        <div class="total-linha">
          <span>Total Bruto:</span>
          <span>{{ formatarPreco(pedido.totalBruto) }}</span>
        </div>
        <div class="total-linha" *ngIf="pedido.desconto > 0">
          <span>Desconto:</span>
          <span class="desconto">-{{ formatarPreco(pedido.desconto) }}</span>
        </div>
        <div class="total-linha total-final">
          <span><strong>Total Líquido:</strong></span>
          <span><strong>{{ formatarPreco(pedido.totalLiquido) }}</strong></span>
        </div>
      </div>
    </div>
  </div>
  
  <div *ngIf="pedidos.length === 0 && !carregando && !erro" class="empty">
    Nenhum pedido encontrado.
  </div>
</div>
