import { Component, OnInit } from '@angular/core';
import { ApiService } from '../../services/api.service';
import { EventoService } from '../../services/evento.service';
import { Promocao, TipoPromocao } from '../../models/promocao.model';

@Component({
  selector: 'app-promocoes',
  templateUrl: './promocoes.component.html',
  styleUrls: ['./promocoes.component.css']
})
export class PromocoesComponent implements OnInit {
  promocoes: Promocao[] = [];
  produtos: any[] = [];
  carregando = false;
  erro = '';
  mostrarFormulario = false;
  promocaoEdicao: Promocao | null = null;

  // Enum para template
  TipoPromocao = TipoPromocao;

  // Formulário
  formulario = {
    produtoId: null as number | null,
    descricao: '',
    tipo: TipoPromocao.Porcentagem,
    valor: 0,
    quantidadeLeve: undefined as number | undefined,
    quantidadePague: undefined as number | undefined,
    dataInicio: '',
    dataFim: '',
    ativo: true
  };

  constructor(
    private apiService: ApiService,
    private eventoService: EventoService
  ) { }

  ngOnInit() {
    this.carregarPromocoes();
    this.carregarProdutos();
  }

  carregarProdutos() {
    this.apiService.obterProdutos().subscribe({
      next: (resultado) => {
        if (resultado.sucesso) {
          this.produtos = resultado.dados;
        }
      },
      error: (error) => {
        console.error('Erro ao carregar produtos:', error);
      }
    });
  }

  carregarPromocoes() {
    this.carregando = true;
    this.erro = '';

    this.apiService.obterPromocoes().subscribe({
      next: (resultado) => {
        if (resultado.sucesso) {
          this.promocoes = resultado.dados;
        } else {
          this.erro = resultado.mensagem;
        }
        this.carregando = false;
      },
      error: (error) => {
        this.erro = 'Erro ao carregar promoções: ' + error.message;
        this.carregando = false;
      }
    });
  }

  mostrarFormularioNovo() {
    this.mostrarFormulario = true;
    this.promocaoEdicao = null;
    this.limparFormulario();
  }

  editarPromocao(promocao: Promocao) {
    this.mostrarFormulario = true;
    this.promocaoEdicao = promocao;
    
    // Buscar o produto vinculado à promoção pelo nome
    let produtoId = null;
    if (promocao.nome) {
      const produto = this.produtos.find(p => p.nome === promocao.nome);
      produtoId = produto ? produto.id : null;
    }
    
    this.formulario = {
      produtoId: produtoId,
      descricao: promocao.descricao,
      tipo: promocao.tipo,
      valor: promocao.valor,
      quantidadeLeve: promocao.quantidadeLeve,
      quantidadePague: promocao.quantidadePague,
      dataInicio: this.formatarDataParaInput(promocao.dataInicio),
      dataFim: this.formatarDataParaInput(promocao.dataFim),
      ativo: promocao.ativo
    };
  }

  salvarPromocao() {
    // Validações
    if (!this.formulario.produtoId) {
      alert('Por favor, selecione um produto para a promoção.');
      return;
    }

    if (!this.formulario.dataInicio) {
      alert('Por favor, selecione a data de início da promoção.');
      return;
    }

    if (!this.formulario.dataFim) {
      alert('Por favor, selecione a data de fim da promoção.');
      return;
    }

    if (new Date(this.formulario.dataInicio) >= new Date(this.formulario.dataFim)) {
      alert('A data de início deve ser anterior à data de fim.');
      return;
    }

    const produtoSelecionado = this.produtos.find(p => p.id === this.formulario.produtoId);
    const nomeProduto = produtoSelecionado ? produtoSelecionado.nome : '';

    if (this.promocaoEdicao) {
      // Atualizar
      const dto = {
        nome: nomeProduto,
        descricao: this.formulario.descricao,
        tipo: Number(this.formulario.tipo),
        valor: this.formulario.valor,
        quantidadeLeve: this.formulario.quantidadeLeve,
        quantidadePague: this.formulario.quantidadePague,
        dataInicio: new Date(this.formulario.dataInicio).toISOString(),
        dataFim: new Date(this.formulario.dataFim).toISOString(),
        ativo: this.formulario.ativo
      };

      this.apiService.atualizarPromocao(this.promocaoEdicao.id, dto).subscribe({
        next: (resultado) => {
          if (resultado.sucesso) {
            this.carregarPromocoes();
            this.carregarProdutos(); // Atualizar produtos para refletir mudanças de preço
            this.cancelarFormulario();
            alert('Promoção atualizada com sucesso!');
          } else {
            alert('Erro: ' + resultado.mensagem);
          }
        },
        error: (error) => {
          alert('Erro ao atualizar promoção: ' + error.message);
        }
      });
    } else {
      // Criar novo
      const dto = {
        nome: nomeProduto,
        descricao: this.formulario.descricao,
        tipo: Number(this.formulario.tipo),
        valor: this.formulario.valor,
        quantidadeLeve: this.formulario.quantidadeLeve,
        quantidadePague: this.formulario.quantidadePague,
        dataInicio: new Date(this.formulario.dataInicio).toISOString(),
        dataFim: new Date(this.formulario.dataFim).toISOString(),
        ativo: this.formulario.ativo,
        produtoIds: [this.formulario.produtoId]
      };

      this.apiService.criarPromocao(dto).subscribe({
        next: (resultado) => {
          if (resultado.sucesso) {
            this.carregarPromocoes();
            this.carregarProdutos(); // Atualizar produtos para refletir mudanças de preço
            this.cancelarFormulario();
            alert('Promoção criada com sucesso!');
          } else {
            alert('Erro: ' + resultado.mensagem);
          }
        },
        error: (error) => {
          alert('Erro ao criar promoção: ' + error.message);
        }
      });
    }
  }

  excluirPromocao(promocao: Promocao) {
    if (confirm(`Deseja excluir a promoção ${promocao.nome}?`)) {
      this.apiService.excluirPromocao(promocao.id).subscribe({
        next: (resultado) => {
          if (resultado.sucesso) {
            this.carregarPromocoes();
            this.carregarProdutos(); // Atualizar produtos para refletir mudanças de preço
            alert('Promoção excluída com sucesso!');
          } else {
            alert('Erro: ' + resultado.mensagem);
          }
        },
        error: (error) => {
          alert('Erro ao excluir promoção: ' + error.message);
        }
      });
    }
  }

  cancelarFormulario() {
    this.mostrarFormulario = false;
    this.promocaoEdicao = null;
    this.limparFormulario();
  }

  private limparFormulario() {
    this.formulario = {
      produtoId: null,
      descricao: '',
      tipo: TipoPromocao.Porcentagem,
      valor: 0,
      quantidadeLeve: undefined,
      quantidadePague: undefined,
      dataInicio: '',
      dataFim: '',
      ativo: true
    };
  }

  private formatarDataParaInput(data: string): string {
    return new Date(data).toISOString().substring(0, 16);
  }

  formatarData(data: string): string {
    return new Date(data).toLocaleString('pt-BR');
  }

  obterDescricaoTipo(tipo: TipoPromocao): string {
    switch (tipo) {
      case TipoPromocao.Porcentagem:
        return 'Porcentagem';
      case TipoPromocao.ValorFixo:
        return 'Valor Fixo';
      case TipoPromocao.LeveXPagueY:
        return 'Leve X Pague Y';
      default:
        return 'Desconhecido';
    }
  }

  formatarValor(promocao: Promocao): string {
    switch (promocao.tipo) {
      case TipoPromocao.Porcentagem:
        return `${promocao.valor}%`;
      case TipoPromocao.ValorFixo:
        return `R$ ${promocao.valor.toFixed(2)}`;
      case TipoPromocao.LeveXPagueY:
        return `Leve ${promocao.quantidadeLeve} Pague ${promocao.quantidadePague}`;
      default:
        return '';
    }
  }

  onTipoChange() {
    if (this.formulario.tipo !== TipoPromocao.LeveXPagueY) {
      this.formulario.quantidadeLeve = undefined;
      this.formulario.quantidadePague = undefined;
    }
  }
}
<div class="promocoes-container">
  <div class="header-actions">
    <h2>Gestão de Promoções</h2>
    <button class="btn-novo" (click)="mostrarFormularioNovo()">
      Nova Promoção
    </button>
  </div>

  <!-- Formulário de Promoção -->
  <div class="formulario-overlay" *ngIf="mostrarFormulario">
    <div class="formulario-modal">
      <h3>{{ promocaoEdicao ? 'Editar Promoção' : 'Nova Promoção' }}</h3>

      <form (ngSubmit)="salvarPromocao()">
        <div class="form-group">
          <label>Produto:</label>
          <select
            [(ngModel)]="formulario.produtoId"
            name="produtoId"
            required>
            <option [value]="null">Selecione um produto</option>
            <option *ngFor="let produto of produtos" [value]="produto.id">
              {{ produto.nome }} ({{ produto.sku }})
            </option>
          </select>
        </div>

        <div class="form-group">
          <label>Descrição:</label>
          <textarea
            [(ngModel)]="formulario.descricao"
            name="descricao"
            rows="3">
          </textarea>
        </div>

        <div class="form-group">
          <label>Tipo:</label>
          <select
            [(ngModel)]="formulario.tipo"
            name="tipo"
            (change)="onTipoChange()"
            required>
            <option [value]="TipoPromocao.Porcentagem">Porcentagem</option>
            <option [value]="TipoPromocao.ValorFixo">Valor Fixo</option>
            <option [value]="TipoPromocao.LeveXPagueY">Leve X Pague Y</option>
          </select>
        </div>

        <div class="form-group" *ngIf="formulario.tipo !== TipoPromocao.LeveXPagueY">
          <label>Valor:</label>
          <input
            type="number"
            [(ngModel)]="formulario.valor"
            name="valor"
            step="0.01"
            min="0"
            required>
          <small *ngIf="formulario.tipo === TipoPromocao.Porcentagem">% de desconto</small>
          <small *ngIf="formulario.tipo === TipoPromocao.ValorFixo">R$ de desconto</small>
        </div>

        <div class="form-row" *ngIf="formulario.tipo === TipoPromocao.LeveXPagueY">
          <div class="form-group">
            <label>Quantidade Leve:</label>
            <input
              type="number"
              [(ngModel)]="formulario.quantidadeLeve"
              name="quantidadeLeve"
              min="1"
              required>
          </div>
          <div class="form-group">
            <label>Quantidade Pague:</label>
            <input
              type="number"
              [(ngModel)]="formulario.quantidadePague"
              name="quantidadePague"
              min="1"
              required>
          </div>
        </div>

        <div class="form-row">
          <div class="form-group">
            <label>Data Início:</label>
            <input
              type="datetime-local"
              [(ngModel)]="formulario.dataInicio"
              name="dataInicio"
              required>
          </div>
          <div class="form-group">
            <label>Data Fim:</label>
            <input
              type="datetime-local"
              [(ngModel)]="formulario.dataFim"
              name="dataFim"
              required>
          </div>
        </div>

        <div class="form-group checkbox-group">
          <label>
            <input
              type="checkbox"
              [(ngModel)]="formulario.ativo"
              name="ativo">
            Promoção Ativa
          </label>
        </div>

        <div class="form-actions">
          <button type="button" class="btn-cancelar" (click)="cancelarFormulario()">
            Cancelar
          </button>
          <button type="submit" class="btn-salvar">
            {{ promocaoEdicao ? 'Atualizar' : 'Criar' }}
          </button>
        </div>
      </form>
    </div>
  </div>

  <div *ngIf="carregando" class="loading">
    Carregando promoções...
  </div>

  <div *ngIf="erro" class="error">
    {{ erro }}
    <button (click)="carregarPromocoes()">Tentar Novamente</button>
  </div>

  <!-- Lista de Promoções -->
  <div class="promocoes-lista" *ngIf="!carregando && !erro">
    <div class="promocao-item" *ngFor="let promocao of promocoes">
      <div class="promocao-info">
        <h3>{{ promocao.nome }}</h3>
        <p class="descricao">{{ promocao.descricao }}</p>
        <div class="promocao-detalhes">
          <span class="tipo">{{ obterDescricaoTipo(promocao.tipo) }}</span>
          <span class="valor">{{ formatarValor(promocao) }}</span>
        </div>
        <div class="promocao-periodo">
          <span>De: {{ formatarData(promocao.dataInicio) }}</span>
          <span>Até: {{ formatarData(promocao.dataFim) }}</span>
        </div>
        <span class="status" [class.ativo]="promocao.ativo" [class.inativo]="!promocao.ativo">
          {{ promocao.ativo ? 'Ativa' : 'Inativa' }}
        </span>
      </div>

      <div class="promocao-actions">
        <button class="btn-editar" (click)="editarPromocao(promocao)">
          Editar
        </button>
        <button class="btn-excluir" (click)="excluirPromocao(promocao)">
          Excluir
        </button>
      </div>
    </div>
  </div>

  <div *ngIf="promocoes.length === 0 && !carregando && !erro" class="empty">
    Nenhuma promoção cadastrada. Clique em "Nova Promoção" para começar.
  </div>
</div>
