import { Component, OnInit } from '@angular/core';
import { ApiService } from '../../services/api.service';
import { CarrinhoService } from '../../services/carrinho.service';
import { Produto } from '../../models/produto.model';

@Component({
  selector: 'app-catalogo',
  templateUrl: './catalogo.component.html',
  styleUrls: ['./catalogo.component.css']
})
export class CatalogoComponent implements OnInit {
  produtos: Produto[] = [];
  carregando = false;
  erro = '';

  constructor(
    private apiService: ApiService,
    private carrinhoService: CarrinhoService
  ) { }

  ngOnInit() {
    this.carregarProdutos();
  }

  carregarProdutos() {
    this.carregando = true;
    this.erro = '';
    
    this.apiService.obterProdutosComEstoque().subscribe({
      next: (resultado) => {
        if (resultado.sucesso) {
          this.produtos = resultado.dados;
        } else {
          this.erro = resultado.mensagem;
        }
        this.carregando = false;
      },
      error: (error) => {
        this.erro = 'Erro ao carregar produtos: ' + error.message;
        this.carregando = false;
      }
    });
  }

  adicionarAoCarrinho(produto: Produto) {
    this.carrinhoService.adicionarItem(produto, 1);
    alert(`${produto.nome} adicionado ao carrinho!`);
  }

  formatarPreco(preco: number): string {
    return preco.toLocaleString('pt-BR', {
      style: 'currency',
      currency: 'BRL'
    });
  }
}
<div class="catalogo-container">
  <h2>Catálogo de Produtos</h2>
  
  <div *ngIf="carregando" class="loading">
    Carregando produtos...
  </div>
  
  <div *ngIf="erro" class="error">
    {{ erro }}
    <button (click)="carregarProdutos()">Tentar Novamente</button>
  </div>
  
  <div class="produtos-grid" *ngIf="!carregando && !erro">
    <div class="produto-card" *ngFor="let produto of produtos">
      <div class="produto-info">
        <h3>{{ produto.nome }}</h3>
        <p class="sku">SKU: {{ produto.sku }}</p>
        <p class="preco">{{ formatarPreco(produto.precoBase) }}</p>
        <p class="estoque">Estoque: {{ produto.estoqueAtual }} unidades</p>
      </div>
      
      <div class="produto-actions">
        <button 
          class="btn-adicionar" 
          (click)="adicionarAoCarrinho(produto)"
          [disabled]="produto.estoqueAtual <= 0">
          {{ produto.estoqueAtual > 0 ? 'Adicionar ao Carrinho' : 'Sem Estoque' }}
        </button>
      </div>
    </div>
  </div>
  
  <div *ngIf="produtos.length === 0 && !carregando && !erro" class="empty">
    Nenhum produto disponível no momento.
  </div>
</div>
